mapper.update:
UPDATE BOARD
SET BOARD_TYPE =  #{updateBoardType}
   ,BOARD_TITLE = #{boardTitle}
   ,BOARD_COMMENT = #{boardComment}
   ,MODIFIER = 'SYSTEM'
   ,MODIFIED_TIME = TO_CHAR(SYSDATE)
WHERE 
BOARD_TYPE = #{boardType}
AND BOARD_NUM = #{boardNum}

mapper.delete:
DELETE BOARD
WHERE
BOARD_TYPE = #{boardType}
AND BOARD_NUM = #{boardNum}
